import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

__archivos__.redessociales.css:
/* Styles specific to the social media banner */

.social-banner-container {
  width: 100%;
  overflow: hidden;
}

.social-carousel {
  display: flex;
  width: max-content; /* Allow content to determine width */
}

@keyframes slide {
  0% {
    transform: translateX(0%);
  }
  100% {
    /* Translate by half the width of the duplicated content */
    transform: translateX(-50%);
  }
}

.animate-slide {
  /* Adjust duration as needed */
  animation: slide 30s linear infinite;
}

.social-item:hover {
  /* Pause animation on individual item hover */
  animation-play-state: paused;
}

/* Pause animation when user hovers over the container */
.social-banner-container:hover .animate-slide {
  animation-play-state: paused;
}

__archivos__.footer.css:
/* Estilos específicos para el footer */

/* Define CSS variables or use Tailwind theme variables */
:root {
  /* Using Shadcn HSL variables defined in globals.css */
  --footer-bg: hsl(var(--muted));
  --footer-text: hsl(var(--muted-foreground));
  --footer-primary: hsl(var(--primary)); /* Mapped in globals.css */
  --footer-accent: hsl(var(--accent)); /* Mapped in globals.css */
  --footer-accent-foreground: hsl(var(--accent-foreground));
  --footer-primary-foreground: hsl(var(--primary-foreground));
  --footer-secondary-foreground: hsl(var(--secondary-foreground));
  --footer-background: hsl(var(--background)); /* Added background for icon hovers */
  --footer-border: hsl(var(--border));
  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;
  --footer-gap: 2rem; /* Gap for the grid */
  --font-size-lg: 1.125rem; /* 18px */
  --font-size-base: 1rem; /* 16px */
  --font-size-sm: 0.875rem; /* 14px */
  --font-weight-bold: 600;
  --transition: all 0.3s ease;
}

.main-footer {
  background: var(--footer-bg);
  color: var(--footer-text);
  border-radius: 0 !important; /* Override potential Shadcn rounding */
  padding: var(--spacing-xl) 0 var(--spacing-md);
  border-top: 1px solid var(--footer-border); /* Added top border */
}

.footer-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); /* Adjusted minmax */
  gap: var(--footer-gap);
  margin-bottom: var(--spacing-xl);
}

/* Logo del footer */
.footer-logo-flex {
  display: flex; /* Ensure flex layout */
  align-items: center; /* Vertically align items */
  gap: 10px !important;
}

.footer-logo-link {
  height: fit-content !important;
  width: fit-content !important;
  display: inline-block; /* Allow image to size correctly */
}

.footer-logo-img {
    max-width: 50px; /* Reduced max-width for better proportion */
    height: auto !important;
    /* margin-bottom: 25px;  Removed, spacing handled by flex gap */
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.15)); /* Softer shadow */
    width: auto !important;
}


.footer-spacer {
  margin-bottom: 20px !important;
}

/* Títulos del footer */
.footer-title {
  font-size: var(--font-size-lg);
  margin-bottom: var(--spacing-lg);
  color: var(--footer-primary); /* Use primary color for titles */
  font-weight: var(--font-weight-bold);
}

/* Enlaces del footer */
.footer-links {
  list-style: none;
  padding: 0;
  margin: 0;
}

.footer-links li {
  margin-bottom: var(--spacing-sm);
  position: relative;
}

.footer-links a {
  color: var(--footer-text);
  display: flex;
  align-items: center;
  transition: var(--transition);
  text-decoration: none;
  font-size: var(--font-size-sm); /* Match overall text size */
}

.footer-links a:hover {
  color: var(--footer-accent); /* Use accent for hover */
  /* Optional: remove transform for cleaner look */
  /* transform: translateX(5px); */
}

/* Dropdown en el footer */
.footer-links .dropdown {
  position: relative;
}

.footer-links .dropdown > a {
  cursor: pointer; /* Indicate interactivity */
}

.footer-links .dropdown-content {
  /* Use padding/margin for nesting instead of display none/block */
  padding-left: var(--spacing-lg);
  margin-top: var(--spacing-xs);
  /* Remove hover logic, handle via Shadcn/Radix components */
}


.footer-links .dropdown-content ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.footer-links .dropdown-content a {
  padding: var(--spacing-xs) 0;
  font-size: 0.85rem; /* Slightly smaller for sub-items */
  color: var(--footer-text); /* Muted color for sub-items */
}
.footer-links .dropdown-content a:hover {
    color: var(--footer-accent); /* Hover for sub-items */
}

/* Información de contacto */
.contact-info {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-md);
  font-size: var(--font-size-sm); /* Match text size */
}

.contact-item {
  display: flex;
  align-items: flex-start; /* Align icon top */
  gap: var(--spacing-sm);
}

.contact-item .lucide { /* Target Lucide icons */
  color: var(--footer-primary); /* Use primary for icons */
  width: 1rem; /* Consistent icon size */
  height: 1rem;
  flex-shrink: 0; /* Prevent icon shrinking */
  margin-top: 0.125rem; /* Slight adjustment for alignment */
}


.whatsapp-link { /* Reusing class for mailto */
  color: var(--footer-text);
  text-decoration: none;
  transition: var(--transition);
}

.whatsapp-link:hover {
  color: var(--footer-accent); /* Use accent for hover */
}

/* Redes sociales */
.social-container {
  margin-top: var(--spacing-lg);
}

.social-title {
  font-size: var(--font-size-base);
  margin-bottom: var(--spacing-sm);
  color: var(--footer-primary); /* Use primary color */
  font-weight: 500;
}

.social-links {
  display: flex;
  gap: var(--spacing-md);
}

.social-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 36px; /* Slightly smaller */
  height: 36px;
  border-radius: 50%;
  background-color: var(--footer-background); /* Use background */
  color: var(--footer-primary); /* Use primary color for icons */
  transition: var(--transition);
  border: 1px solid var(--footer-border); /* Add subtle border */
}
.social-icon:hover {
  background-color: var(--footer-accent);
  color: var(--footer-accent-foreground); /* Use accent foreground */
  transform: translateY(-2px); /* Slightly less movement */
  border-color: transparent; /* Remove border on hover */
}


/* Parte inferior del footer */
.footer-bottom {
  border-top: 1px solid var(--footer-border);
  padding-top: var(--spacing-md);
  text-align: center;
  margin-top: var(--spacing-xl); /* Add margin top */
}

.copyright {
  font-size: var(--font-size-sm);
  color: var(--footer-text); /* Use muted foreground */
  margin-bottom: 0;
}

/* Botón volver arriba */
.back-to-top {
  position: fixed;
  bottom: 30px;
  right: 30px;
  width: 40px; /* Adjusted size */
  height: 40px;
  border-radius: 50%;
  background-color: var(--footer-primary); /* Use primary color */
  color: var(--footer-primary-foreground); /* Use primary foreground */
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  opacity: 0;
  visibility: hidden;
  transition: opacity 0.3s ease, visibility 0.3s ease, transform 0.3s ease; /* Smoother transition */
  z-index: 999;
  border: none;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.15); /* Adjusted shadow */
}

.back-to-top.visible {
  opacity: 1;
  visibility: visible;
}

.back-to-top:hover {
  background-color: var(--footer-accent); /* Use accent color on hover */
  color: var(--footer-accent-foreground);
  transform: translateY(-3px);
}

/* Responsive */
@media (max-width: 768px) {
  .footer-grid {
    grid-template-columns: 1fr; /* Stack columns on mobile */
    gap: var(--spacing-xl);
    text-align: center; /* Center align text in columns */
  }

  .footer-logo-flex {
      justify-content: center; /* Center logo section */
  }

  .footer-title {
      text-align: center; /* Center titles */
  }

  .footer-links li, .footer-links .dropdown > a {
      justify-content: center; /* Center links */
  }
  .footer-links .dropdown-content {
      padding-left: 0; /* Remove indent for sub-items */
      text-align: center; /* Center sub-items */
  }

  .contact-info {
    align-items: center; /* Center contact items */
  }
  .contact-item {
    justify-content: center; /* Center contact items */
  }

  .social-container {
      text-align: center; /* Center social title */
  }

  .social-links {
    justify-content: center; /* Center social icons */
  }

  .back-to-top {
    bottom: 20px;
    right: 20px;
    width: 36px; /* Slightly smaller */
    height: 36px;
  }
    .back-to-top .lucide {
        width: 1rem; /* Adjust icon size */
        height: 1rem;
    }
}

